<template>
	<el-container>
		<el-aside class="sidebar" width="250px">
			<router-link class="menu-logo" :to="{path:'/'}">
				<img src="data:image/svg+xml;base64," alt="">

				<h1>Ant Design Pro</h1>
			</router-link>

			<div class="user-info">
				<div class="user-avatar">
					<img :src="user.avatar" alt="">
				</div>
				{{user.name}}
			</div>


			<!-- 侧边导航栏 -->
			<div class="nav">
				<sy-menu-item></sy-menu-item>
			</div>

			<div class="background"></div>
		</el-aside>
		<el-container style="background: #eee;height: 100vh;overflow: hidden;">
			<el-header class="container-header">

				<!-- 此处为顶部导航栏内容 -->
				<div class="navbar">
					<i class="el-icon-s-unfold"></i>

					<div>
						<ul class="navbar-right">
							<li>
								<i class="el-icon-s-unfold"></i>
							</li>
							<li>
								<i class="el-icon-s-unfold"></i>
							</li>
							<li>
								<el-dropdown @command="userCommand">
									<span class="el-dropdown-link">
										<i class="el-dropdown-icon el-icon-user-solid"></i>
									</span>
									<el-dropdown-menu slot="dropdown">
										<el-dropdown-item command="profile">个人中心</el-dropdown-item>
										<el-dropdown-item command="setting">系统设置</el-dropdown-item>
										<el-dropdown-item command="logout">退出登录</el-dropdown-item>
									</el-dropdown-menu>
								</el-dropdown>
							</li>
						</ul>
					</div>
				</div>

				<!-- 此处为顶部下面的面包屑 -->
				<div class="expand">
					<slot name="breadcrumb">
						<el-breadcrumb class="breadcrumb" separator-class="el-icon-arrow-right">
							<el-breadcrumb-item :to="{ path: '/' }">首页</el-breadcrumb-item>

							<el-breadcrumb-item v-for="(breadcrumb, index) in breadcrumbs" :key="'breadcrumb-'+index">{{breadcrumb.name}}</el-breadcrumb-item>
						</el-breadcrumb>
					</slot>

					<slot name="breadcrumb-after">
					</slot>
				</div>
			</el-header>
			<el-main class="content-box">
				<slot></slot>
			</el-main>
			<!-- <el-footer><slot name="footer">Footer</slot></el-footer> -->
		</el-container>
	</el-container>
</template>

<script>
import syMenuItem from "@/components/layout/sy-menu-item.vue";

export default {
	name: "app",
	components: {
		syMenuItem
	},
	data(){
		return {
			menus: [],
			breadcrumbs: [],
			user: {
				name: '',
				avatar: ''
			}
		}
	},
	created(){
		let meta = this.$route.meta;

		// 设置浏览器标题
		window.document.title = meta.title;

		// 激活路由
		this.$store.commit('access/active',meta);

		// 设置面包屑数据
		this.breadcrumbs = this.$store.getters['access/breadcrumb'];

		// 获取用户信息
		let user = this.$store.getters['auth/user'];

		// 验证用户信息
		if(!user){
			this.$store.commit('auth/logout');
			return false;
		}

		user.avatar = 'http://127.0.0.1:8000/static/api/avatar/20.png';
		this.user = user;

	},

	methods:{
		userCommand(command){
			// 退出登录
			if(command == 'logout'){
				this.$store.commit('auth/logout');
			}
		}
	}
};
</script>

