<template>
	<div class="layout-aside" :class="asideClass" @mouseleave="mouseleave" @mouseenter="mouseenter">
		{{this.sidebar_mini}}
		<div class="slider">
			<el-scrollbar class="layout-aside-scroll">
				<router-link class="menu-logo" :to="{path:'/'}">
					<img class="logo-img" src="data:image/svg+xml;base64," alt="">

					<h1 class="logo-title">Admin Dashboard</h1>
				</router-link>

				<div class="user-info">
					<div class="user-avatar">
						<img :src="user.avatar" alt="">
					</div>
					<span class="user-name">{{user.name}}</span>
				</div>

				<!-- 侧边导航栏 -->
				<div class="nav">
					<menu-item :menus="menus" :lv="0" ref="menu_item"></menu-item>
				</div>

				<div class="background" :style="[backgroundStyle]"></div>
			</el-scrollbar>
		</div>
	</div>
</template>

<script>
import MenuItem from "./menu-item.vue";
import { debounce } from '@/libs/util.js';
import { mapState } from 'vuex'

export default {
	name: "layout-aside",
	components: {
		MenuItem,
	},
	data(){
		return {
			is_mini: false,

			is_leave: false,

			// menus: [],

			user: {
				name: '',
				avatar: ''
			}
		}
	},
	created(){
		// 获取用户信息
		let user = this.$store.getters['auth/user'];

		// 验证用户信息
		if(!user){
			this.$store.commit('auth/logout');
			return false;
		}

		this.user = user;
		// this.menus = this.$store.state.access.menus;

		// 在初次加载时, 如果侧边栏为最小化, 则关闭展开的
		if(this.sidebar_mini){
			this.is_leave = true;
			this.closeMenu();
		}
	},

	mounted(){
	},

	methods:{
		mouseenter(){
			this.is_leave = false;
		},

		mouseleave(){
			this.is_leave = true;
			this.closeMenu();
		},

		closeMenu: debounce(300, function(){
			if(this.sidebar_mini && this.is_leave){
				this.$refs.menu_item.close()
			}
		})
	},
	computed: {
		menus: {
			get() {
				// console.log(this.$store.state.access.menus);
				return this.$store.state.access.menus;
			}
		},

		...mapState('settings', {
			sidebar_mini: state =>state.sidebar_mini,
			filters_color: state =>state.sidebar_filters_color,
			background_color: state =>state.sidebar_background_color,
			background_img: state =>state.sidebar_background_img,
		}),

		asideClass(){
			return {
				'layout-aside-mini': this.sidebar_mini
			}
		},

		backgroundStyle(){
			return {
				backgroundImage: 'url(' + this.background_img + ')',
				'--bgcolor': this.background_color
			}
		}
	},
	watch: {
		sidebar_mini(val){
			if(true == val){
				this.is_leave = val;

				this.closeMenu();
			}
		}
	}
}
</script>

